Table "Users" { "id" SERIAL [pk, increment] "name" VARCHAR(255) [not null] "email" VARCHAR(255) [not null] "created_at" TIMESTAMP [default: `CURRENT_TIMESTAMP`] "gender" VARCHAR(10) } Table "MonthlyBills" { "id" SERIAL [pk, increment] "user_id" INT "total_amount" DECIMAL(10, 2) "status" VARCHAR(20) "month" VARCHAR(20) } Table "BillDetails" { "id" SERIAL [pk, increment] "bill_id" INT "name" VARCHAR(255) [not null] "price" DECIMAL(10, 2) "quantity" INT } Table "RecurringBills" { "id" SERIAL [pk, increment] "user_id" INT "name" VARCHAR(255) [not null] "amount" DECIMAL(10, 2) "active" BOOLEAN [default: TRUE] } Ref :"Users"."id" < "MonthlyBills"."user_id" Ref :"MonthlyBills"."id" < "BillDetails"."bill_id" Ref :"Users"."id" < "RecurringBills"."user_id"